#!/usr/bin/env bash
rm -rf ../mac/ToneWriter.app & rm -rf ../mac/ToneWriter.app.zip & jar2app ToneWriter.jar -n "ToneWriter" -d "ToneWriter" -i TWIcon.icns -b com.tac550.tonewriter -v 0.4 -s 0.4 ../mac/ToneWriter.app && cp -a '../../Built-in Tones' '../mac/ToneWriter.app/Contents/Built-in Tones' && cp -a '../../licenses' '../mac/ToneWriter.app/Contents/licenses' && cd ../mac/ && zip -r ToneWriter.app.zip ToneWriter.app
